import{_ as o,c as t,o as e,a2 as s}from"./chunks/framework.DYuVWWma.js";const r="/assets/mogan-gaoliang.Ch6WbFQb.png",i="/assets/mogan-banshou.Cnn94KCF.png",n="/assets/mogan-xinjiankuaijiejian.Be9M4PhD.png",l="/assets/mogan-kuaijiejianxiaoguo.Bb56YUnQ.png",c="/assets/mokeshequ-QQgroup.DmaBZumF.png",d="/assets/discord-QRcode.C_kQfhJe.png",f=JSON.parse('{"title":"标记类结构自定义快捷键操作教程","description":"","frontmatter":{},"headers":[],"relativePath":"blog/mokeshalong-kuaijiejian.md","filePath":"blog/mokeshalong-kuaijiejian.md","lastUpdated":1747067415000}'),g={name:"blog/mokeshalong-kuaijiejian.md"};function p(h,a,m,_,u,k){return e(),t("div",null,a[0]||(a[0]=[s('<h1 id="标记类结构自定义快捷键操作教程" tabindex="-1">标记类结构自定义快捷键操作教程 <a class="header-anchor" href="#标记类结构自定义快捷键操作教程" aria-label="Permalink to &quot;标记类结构自定义快捷键操作教程&quot;">​</a></h1><p>本教程为墨客沙龙分享教学，将指导如何自定义标记类结构的快捷键，需注意不同模式下同一快捷键可能对应不同效果。目前有两种配置方式：</p><h2 id="一、通过菜单栏配置快捷键" tabindex="-1">一、通过菜单栏配置快捷键 <a class="header-anchor" href="#一、通过菜单栏配置快捷键" aria-label="Permalink to &quot;一、通过菜单栏配置快捷键&quot;">​</a></h2><ol><li><strong>插入标记</strong>：点击【插入】-插入需定义快捷键的标记。</li><li><strong>打开快捷键设置</strong>：点击菜单栏【标记的首选项】（扳手图标），选择【新建快捷键】。</li><li><strong>设定快捷键</strong>：在快捷键输入区域，输入自定义快捷键，点击【新增】，完成后点击【退出】。</li></ol><h2 id="二、通过工具菜单配置快捷键" tabindex="-1">二、通过工具菜单配置快捷键 <a class="header-anchor" href="#二、通过工具菜单配置快捷键" aria-label="Permalink to &quot;二、通过工具菜单配置快捷键&quot;">​</a></h2><p>在【工具】-【键盘】-【编辑键盘快捷键】中，按 <code>(make &#39;对应的标记代码)</code> 格式输入命令进行配置。例如，若需为高亮标记设置快捷键，输入 <code>(make &#39;marked)</code> 即可。</p><h2 id="【案例】通过菜单栏配置【高亮】快捷键" tabindex="-1">【案例】通过菜单栏配置【高亮】快捷键 <a class="header-anchor" href="#【案例】通过菜单栏配置【高亮】快捷键" aria-label="Permalink to &quot;【案例】通过菜单栏配置【高亮】快捷键&quot;">​</a></h2><p>以下以语义标记中的【高亮】为例，介绍菜单栏配置流程。</p><ol><li><strong>插入标记</strong>：点击【插入】-【语义标记】-【高亮】，插入需定义快捷键的标记。</li></ol><p><img src="'+r+'" alt="插入想要定义快捷键的标记"></p><ol><li><strong>打开快捷键设置</strong>：点击菜单栏【标记的首选项】（扳手图标），选择【新建快捷键】。</li></ol><p><img src="'+i+'" alt="点击扳手图标"></p><ol start="3"><li><strong>设定快捷键</strong>：在快捷键输入区域，输入自定义快捷键（例如 <code>Ctrl+B</code>），点击【新增】，完成后点击【退出】。</li></ol><p><img src="'+n+'" alt="输入自定义的快捷键"></p><ol start="4"><li><strong>应用快捷键</strong>：选中目标文本，按下设定的快捷键，即可实现文本高亮。</li></ol><p><img src="'+l+'" alt="快捷键生效"> 若想进一步深入了解相关技术原理及更多应用场景，可观看墨客沙龙的相关视频：<a href="https://www.bilibili.com/video/BV122EGz5Ej4/" target="_blank" rel="noreferrer">墨客社区沙龙：快捷键自定义和宏定义教学</a>。视频中包含对相关技术更为全面深入的解读，助力您更高效地掌握和运用标记类结构的自定义快捷键功能。</p><p>可扫描下列二维码加入墨客社区QQ群和Discord频道，参与后续沙龙。</p><h3 id="qq群" tabindex="-1"><strong>QQ群</strong> <a class="header-anchor" href="#qq群" aria-label="Permalink to &quot;**QQ群**&quot;">​</a></h3><p><img src="'+c+'" alt="QQ群"></p><h3 id="discord频道" tabindex="-1"><strong>Discord频道</strong> <a class="header-anchor" href="#discord频道" aria-label="Permalink to &quot;**Discord频道**&quot;">​</a></h3><p><img src="'+d+'" alt="QQ群"></p>',21)]))}const q=o(g,[["render",p]]);export{f as __pageData,q as default};
