import{_ as t,c as e,o as s,a2 as i}from"./chunks/framework.BiHThamv.js";const l="/assets/llm_session.quT4HntU.png",o="/assets/llm_ask_in_rendered_format.DLI_5Y3L.png",r="/assets/tabular_as_input.DWnj-M5a.png",n="/assets/jiangyanginppt.DhOlE4n2.png",p="/assets/read_doc.BshYkHP-.png",f=JSON.parse('{"title":"AI大模型集成功能使用指南","description":"","frontmatter":{},"headers":[],"relativePath":"docs/guide-tuto-llm-plug.md","filePath":"docs/guide-tuto-llm-plug.md","lastUpdated":1745472718000}'),d={name:"docs/guide-tuto-llm-plug.md"};function c(h,a,_,u,m,g){return s(),e("div",null,a[0]||(a[0]=[i('<h1 id="ai大模型集成功能使用指南" tabindex="-1">AI大模型集成功能使用指南 <a class="header-anchor" href="#ai大模型集成功能使用指南" aria-label="Permalink to &quot;AI大模型集成功能使用指南&quot;">​</a></h1><p>关于AI大模型集成功能的安装请参考 <a href="./install-llm-plugin.html">AI大模型集成功能安装指南</a>。</p><h2 id="文本-数学-表格等作为输入" tabindex="-1">文本，数学，表格等作为输入 <a class="header-anchor" href="#文本-数学-表格等作为输入" aria-label="Permalink to &quot;文本，数学，表格等作为输入&quot;">​</a></h2><h3 id="导入大模型块" tabindex="-1">导入大模型块 <a class="header-anchor" href="#导入大模型块" aria-label="Permalink to &quot;导入大模型块&quot;">​</a></h3><p>您可以遵循下图插入一个大模型的块 (Session) <img src="'+l+'" alt="llm_session"></p><p>大部分的网页端大模型只支持纯文本输入，然而在Liii STEM里，你可以把<strong>任何</strong>渲染的格式作为大模型的输入。我们会将您给模型的输入渲染成蓝色。如下图所示：</p><p><img src="'+o+'" alt="input_in_rendered_format"></p><p>此功能不仅支持数学公式，甚至支持表格，如下图所示</p><p><img src="'+r+'" alt="table"></p><h2 id="可编辑的大模型输出" tabindex="-1">可编辑的大模型输出 <a class="header-anchor" href="#可编辑的大模型输出" aria-label="Permalink to &quot;可编辑的大模型输出&quot;">​</a></h2><p>一个常见的在使用大模型的时候的问题就是对模型的回答进行整理归档以及进一步提问。</p><p>Liii STEM内置的大模型的输出可以直接在Liii STEM里进行渲染，方便您直接进行编辑和修改。如下图所示，您可以用Liii STEM自动出卷子和作业答案！：</p><p><img src="'+n+'" alt="simple"></p><h2 id="读取文件作为llm输入" tabindex="-1">读取文件作为LLM输入 <a class="header-anchor" href="#读取文件作为llm输入" aria-label="Permalink to &quot;读取文件作为LLM输入&quot;">​</a></h2><p>在使用大模型时，我们常需要分析或处理已有文档。Liii STEM支持直接读取文件作为LLM的输入内容。</p><h3 id="使用方法" tabindex="-1">使用方法 <a class="header-anchor" href="#使用方法" aria-label="Permalink to &quot;使用方法&quot;">​</a></h3><p>使用以下格式读取文件：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>%include ~/path/to/your/file</span></span></code></pre></div><p>例如： <img src="'+p+'" alt="read_doc"></p><p>注意：路径支持绝对路径。文件内容会自动作为LLM的输入内容。</p><h2 id="faq" tabindex="-1">FAQ <a class="header-anchor" href="#faq" aria-label="Permalink to &quot;FAQ&quot;">​</a></h2><p>Q: 可以使用中文问答吗？ A：可以，需要先将文档语言设置为中文。</p>',22)]))}const q=t(d,[["render",c]]);export{f as __pageData,q as default};
