import{_ as s,c as i,a2 as t,o}from"./chunks/framework.Dk6d6BkW.js";const e="/assets/macos_1.e8osSyhS.png",c="/assets/macos_2.DgR2D_LK.png",m="/assets/macos_3.BRzPif0u.png",r="/assets/macos_4.DF0rPcuh.png",l="/assets/macos_5.DUoGF-fY.png",n="/assets/macos_6.DSDkk94q.png",p="/assets/macos_7.C97IfBcU.png",M=JSON.parse('{"title":"如何在macOS上安装LiiiSTEM","description":"","frontmatter":{},"headers":[],"relativePath":"guide/docs/tutorials/install-course/macos.md","filePath":"guide/docs/tutorials/install-course/macos.md","lastUpdated":1744785249000}'),d={name:"guide/docs/tutorials/install-course/macos.md"};function h(_,a,g,u,S,f){return o(),i("div",null,a[0]||(a[0]=[t('<h1 id="如何在macos上安装liiistem" tabindex="-1">如何在macOS上安装LiiiSTEM <a class="header-anchor" href="#如何在macos上安装liiistem" aria-label="Permalink to &quot;如何在macOS上安装LiiiSTEM&quot;">​</a></h1><h2 id="第一步-确定macos的版本和架构" tabindex="-1">第一步：确定macOS的版本和架构 <a class="header-anchor" href="#第一步-确定macos的版本和架构" aria-label="Permalink to &quot;第一步：确定macOS的版本和架构&quot;">​</a></h2><p>本文以 macOS 15.3.2 (Apple M1芯片）为例。</p><p><img src="'+e+'" alt="macos下载"></p><p>Apple M1芯片是苹果推出的新的芯片，属于arm64架构。老款的苹果电脑使用的是Intel的芯片，属于x64/x86架构。不同的架构上的软件是不兼容的，故而需要下载不同的dmg安装包。</p><h2 id="第二步-下载安装包" tabindex="-1">第二步：下载安装包 <a class="header-anchor" href="#第二步-下载安装包" aria-label="Permalink to &quot;第二步：下载安装包&quot;">​</a></h2><p>访问官方下载页面：<a href="./../../../install.html">点击前往</a></p><p>在LiiiSTEM的安装页面，选择 macOS arm64 的安装包或者 macOS Arm的安装包。请先确定macOS的版本和架构！</p><h2 id="第三步-通过dmg安装包安装liiistem" tabindex="-1">第三步：通过dmg安装包安装LiiiSTEM <a class="header-anchor" href="#第三步-通过dmg安装包安装liiistem" aria-label="Permalink to &quot;第三步：通过dmg安装包安装LiiiSTEM&quot;">​</a></h2><p>在苹果笔记本上安装dmg格式的安装包，只需要双击dmg格式的安装包。</p><p>双击之后弹出如下界面，此时将此界面关闭即可： <img src="'+c+'" alt="macos下载"> 打开macOS系统的文件浏览器访达，在左侧可以找到刚刚挂载的dmg映像（dmg格式相当于过去的光盘）： <img src="'+m+'" alt="macos下载"> 将LiiiSTEM拖动到左侧的应用程序就完成了LiiiSTEM的安装</p><h2 id="第四步-解决无法验证的开发者的问题" tabindex="-1">第四步：解决无法验证的开发者的问题 <a class="header-anchor" href="#第四步-解决无法验证的开发者的问题" aria-label="Permalink to &quot;第四步：解决无法验证的开发者的问题&quot;">​</a></h2><p>成为macOS系统的验证的开发者需要固定向苹果公司支付一笔费用，由于LiiiSTEM是自由软件，我们并没有支付这一笔费用。所以用户需要解决如下问题： <img src="'+r+'" alt="macos下载"> 在系统设置-&gt;隐私与安全性找到如下提示，点击【仍要打开】，就可以初次打开LiiiSTEM <img src="'+l+'" alt="macos下载"><img src="'+n+'" alt="macos下载"> 再次打开LiiiSTEM的时候，就不会出现安全性的提示了。</p><h2 id="第五步-双击打开软件就可以使用啦" tabindex="-1">第五步：双击打开软件就可以使用啦！！ <a class="header-anchor" href="#第五步-双击打开软件就可以使用啦" aria-label="Permalink to &quot;第五步：双击打开软件就可以使用啦！！&quot;">​</a></h2><p><img src="'+p+'" alt="macos下载"></p>',15)]))}const b=s(d,[["render",h]]);export{M as __pageData,b as default};
