import{_ as e,c as t,a0 as i,o as r}from"./chunks/framework.BL9wBnZA.js";const s="/assets/magic_paste_1.BOeAq-Xv.png",l="/assets/magic_paste_2.B6QeZ0Hi.png",o="/assets/magic_paste_3.DHf5o4jx.png",u=JSON.parse('{"title":"Liii STEM的魔法粘贴","description":"","frontmatter":{},"headers":[],"relativePath":"guide/tutorials/magic_paste.md","filePath":"guide/tutorials/magic_paste.md","lastUpdated":1743002866000}'),d={name:"guide/tutorials/magic_paste.md"};function c(n,a,p,h,m,b){return r(),t("div",null,a[0]||(a[0]=[i('<h1 id="liii-stem的魔法粘贴" tabindex="-1">Liii STEM的魔法粘贴 <a class="header-anchor" href="#liii-stem的魔法粘贴" aria-label="Permalink to &quot;Liii STEM的魔法粘贴&quot;">​</a></h1><h2 id="魔法粘贴和智能粘贴的差异" tabindex="-1">魔法粘贴和智能粘贴的差异 <a class="header-anchor" href="#魔法粘贴和智能粘贴的差异" aria-label="Permalink to &quot;魔法粘贴和智能粘贴的差异&quot;">​</a></h2><h3 id="魔法粘贴功能" tabindex="-1">魔法粘贴功能 <a class="header-anchor" href="#魔法粘贴功能" aria-label="Permalink to &quot;魔法粘贴功能&quot;">​</a></h3><p>魔法粘贴提供了对数学公式和表格的无损复制粘贴功能，您可以从各类大语言模型(LLM)中直接复制内容到Liii STEM的TMU文档中。 系统会自动将LLM生成的内容（包括代码、公式、表格等）智能转换为标准格式，确保文档排版的专业性和一致性。</p><h3 id="智能粘贴功能" tabindex="-1">智能粘贴功能 <a class="header-anchor" href="#智能粘贴功能" aria-label="Permalink to &quot;智能粘贴功能&quot;">​</a></h3><p>快捷键：<kbd>ctrl</kbd> + <kbd>shift</kbd> + <kbd>v</kbd></p><ul><li>在文本模式下，执行纯文本粘贴</li><li>在数学模式下，自动将LaTeX代码转换为Liii STEM数学公式</li></ul><h2 id="魔法粘贴使用指南" tabindex="-1">魔法粘贴使用指南 <a class="header-anchor" href="#魔法粘贴使用指南" aria-label="Permalink to &quot;魔法粘贴使用指南&quot;">​</a></h2><ol><li>在LLM（目前支持DeepSeek、豆包，后续开发会逐渐支持更多LLM）或知乎中选中你想要复制的内容，<kbd>ctrl</kbd> + <kbd>c</kbd> 或右键复制，如图所示： <img src="'+s+'" alt="魔法粘贴1"></li></ol><blockquote><p>注意！不要直接点击DeepSeek左下角的复制，界面上的复制按钮，实际上复制得到的是Markdown格式。</p></blockquote><ol start="2"><li>在Liii STEM中直接 <kbd>ctrl</kbd> + <kbd>v</kbd> ，效果如图所示： <img src="'+l+'" alt="魔法粘贴2"></li></ol><h2 id="智能粘贴使用指南" tabindex="-1">智能粘贴使用指南 <a class="header-anchor" href="#智能粘贴使用指南" aria-label="Permalink to &quot;智能粘贴使用指南&quot;">​</a></h2><p>具体操作步骤如下图所示（可通过右键点击图片放大查看）： <img src="'+o+'" alt="魔法粘贴3"></p>',13)]))}const _=e(d,[["render",c]]);export{u as __pageData,_ as default};
