import{_ as a,c as r,o as i,a3 as t}from"./chunks/framework.BdEUqdnJ.js";const o="/assets/news-moerxiancheng-202503-1.Bh9swfxD.jpg",s="/assets/news-moerxiancheng-202503-2.BfvUoYrI.jpg",T=JSON.parse('{"title":"三鲤网络走进摩尔线程","description":"","frontmatter":{},"headers":[],"relativePath":"news/moerxiancheng.md","filePath":"news/moerxiancheng.md","lastUpdated":1745472718000}'),n={name:"news/moerxiancheng.md"};function c(p,e,h,l,d,_){return i(),r("div",null,e[0]||(e[0]=[t('<h1 id="三鲤网络走进摩尔线程" tabindex="-1">三鲤网络走进摩尔线程 <a class="header-anchor" href="#三鲤网络走进摩尔线程" aria-label="Permalink to &quot;三鲤网络走进摩尔线程&quot;">​</a></h1><blockquote><p>近日，三鲤网络CEO沈达、COO李岩松率公司核心研发团队赴国内GPU芯片领军企业摩尔线程（Moore Threads）进行参观交流。双方围绕“AI技术赋能科技写作”展开深度对话，三鲤网络团队现场演示了旗下科技写作产品Liii STEM，并基于摩尔线程AIPC展现了其卓越性能，获得摩尔线程相关团队高度评价。 <br></p></blockquote><hr><p>  近日，三鲤网络CEO沈达、COO李岩松博士率公司核心研发团队赴国内GPU芯片领军企业摩尔线程（Moore Threads）进行参观交流。在摩尔线程北京总部展厅内，三鲤网络研发团队通过摩尔线程AIPC产品现场演示了Liii STEM的核心功能并现场将使用摩尔线程显卡运行的大模型集成于Liii STEM。Liii STEM凭借“零代码”AI交互写作设计与智能公式编辑，实现了复杂科学写作“所见即所得”，大幅降低科技写作的门槛，极快地加速LaTeX，Word等常用软件写作。 摩尔线程相关负责人表示：“Liii STEM将复杂的代码转化为直观的交互体验，其公式编辑功能精准匹配科技写作需求，展现了AI技术与科技写作深度融合的潜力。” <br></p><p><img src="'+o+'" alt="三鲤网络参观现场"></p><p><img src="'+s+'" alt="三鲤网络参观现场"></p><p>  三鲤网络始终致力于通过智能化工具推动写作革新，让科技写作如对话般流畅；而摩尔线程凭借自主研发的全功能GPU芯片及软硬一体算力解决方案，已成为国内AI计算领域的重要力量。此次交流旨在探索双方技术协同的可能性，为科技写作行业提供更高效、更智能的解决方案。 <br></p><h2 id="关于三鲤网络" tabindex="-1">关于三鲤网络 <a class="header-anchor" href="#关于三鲤网络" aria-label="Permalink to &quot;关于三鲤网络&quot;">​</a></h2><p>三鲤网络致力于提高用户在科技写作上的质量和效率。依托中国科学技术大学-德清阿尔法研究院的创业孵化器，基于自研编程语言Goldfish Scheme发布智能科技写作软件Liii STEM。<br></p><h2 id="关于摩尔线程" tabindex="-1">关于摩尔线程 <a class="header-anchor" href="#关于摩尔线程" aria-label="Permalink to &quot;关于摩尔线程&quot;">​</a></h2><p>摩尔线程是国内领先的GPU芯片设计企业，专注于提供全栈GPU计算解决方案，产品广泛应用于人工智能、云计算、图形渲染等前沿领域。</p>',11)]))}const f=a(n,[["render",c]]);export{T as __pageData,f as default};
